import { createAsyncThunk, createSlice } from "@reduxjs/toolkit";
import Axios from 'axios';
export const postOrder = createAsyncThunk("orders/postOrder", async (data) => {
  const response = await Axios.post("http://localhost:8080/api/v1/uploadOrderFile", data,                
  {
    headers: {
        "Content-type": "multipart/form-data;boundary=----------287032381131322",
    },                    
}
);
  const order = await response.json();
  console.log(order);
  return order;
});

const ordersSlice = createSlice({
  name: "orders",
  initialState: {
    loading: false,
    order: null,
  },
  reducers: {
    orderUploading(state, action) {
        state.loading=true;
    },
    orderUploaded(state, action) {
        state.loading=false;
        state.order=action.payload;
    }
  },
  extraReducers: {
    [postOrder.pending]: (state, action) => {
      state.loading = true;
    },
    [postOrder.fulfilled]: (state, action) => {
      state.loading = false;
      console.log(action.payload);
      state.order = action.payload;
    },
    [postOrder.rejected]: (state, action) => {
      state.loading = false;
    },
  },
});

export const { orderUploading, orderUploaded } = ordersSlice.actions;

export default ordersSlice.reducer;
