import { useDispatch, useSelector } from "react-redux";

import { useHistory } from "react-router-dom";
import { useState } from "react";
import { productAdded } from "./productsSlice";

export function AddProduct() {
  const dispatch = useDispatch();
  const history = useHistory();

  const [sku, setSKU] = useState("");
  const [name, setName] = useState("");
  const [quantity, setQuantity] = useState(0);
  const [unitPrice, setUnitPrice] = useState(0);
  const [error, setError] = useState(null);

  const handleSKU = (e) => setSKU(e.target.value);
  const handleName = (e) => setName(e.target.value);
  const handleQuantity = (e) => setQuantity(e.target.value);

  const productsAmount = useSelector((state) => state.products.entities.length);

  const handleClick = () => {
    if (name && sku && quantity && unitPrice) {
      dispatch(
        productAdded({
          sku,
          name,
          quantity,
          unitPrice,
        })
      );

      setError(null);
      history.push("/");
    } else {
      setError("Fill in all fields");
    }

    setName("");
    setQuanity(0);
    setUnitPrice(0);
  };

  return (
    <div className="container">
      <div className="row">
        <h1>Add product</h1>
      </div>
      <div className="row">
        <div className="three columns">
          <label htmlFor="skuInput">SKU</label>
          <input
            className="u-full-width"
            type="text"
            placeholder=""
            id="skuInput"
            onChange={handleSKU}
            value={sku}
          />
         <label htmlFor="nameInput">Name</label>
          <input
            className="u-full-width"
            type="text"
            placeholder=""
            id="nameInput"
            onChange={handleName}
            value={name}
          />
          <label htmlFor="quantityInput">Quantity</label>
          <input
            className="u-full-width"
            type="text"
            placeholder=""
            id="quantityInput"
            onChange={handleQuantity}
            value={quantity}
          />
          <label htmlFor="unitPriceInput">Unit Price</label>
          <input
            className="u-full-width"
            type="text"
            placeholder=""
            id="unitPriceInput"
            onChange={handleUnitPrice}
            value={unitPrice}
          />
          {error && error}
          <button onClick={handleClick} className="button-primary">
            Add product
          </button>
        </div>
      </div>
    </div>
  );
}
