import { Button } from 'react-bootstrap';
import './ProductList.css';
import { useDispatch, useSelector } from "react-redux";
import { fetchProducts, productDeleted } from "./ProductsSlice";
import { Link } from "react-router-dom";
export function ProductList() {
  const dispatch = useDispatch();

  const { entities } = useSelector((state) => state.products);
  const loading = useSelector((state) => state.loading);

  const handleDelete = (id) => {
    dispatch(productDeleted({ id }));
  };
    return (
      <div className="container">
        <div className="row">
          <h1>Inventory Management</h1>
        </div>

        <div className="row">
        <div className="two columns">
          <Button
            onClick={() => dispatch(fetchProducts())}
            className="button-primary"
          >
            Load inventory
          </Button>
        </div>
        <div className="two columns">
          <Link to="/add-product">
            <Button className="button-primary">Add product</Button>
          </Link>
        </div>
      </div>
        <div className="row">
          <table>
            <thead>
              <tr>
                <th>SKU</th>
                <th>Name</th>
                <th>Quantity</th>
                <th>Unit Price</th>
                <th>Operation</th>
              </tr>
            </thead>
            <tbody>
            {entities.length &&
                entities.map(({ sku, name, quantity, unit_price }, i) => (
                  <tr key={i}>
                    <td>{sku}</td>
                    <td>{name}</td>
                    <td>{quantity}</td>
                    <td>{unit_price}</td>
                    <td>
                      <Button onClick={() => handleDelete(sku)}>Delete</Button>
                      <div class="divider"></div>
                      <Link to={`/edit-user/${sku}`}>
                        <Button>Edit</Button>
                      </Link>
                    </td>
                  </tr>
                ))}
            </tbody>
          </table>
        </div>
      </div>
    );
  }
