import { createAsyncThunk, createSlice } from "@reduxjs/toolkit";
import Axios from 'axios';
export const postOrder = createAsyncThunk("orders/postOrder", async (data) => {
  const response = await Axios.post("http://localhost:8080/api/v1/uploadOrderFile", data,                
  {
    headers: {
        "Content-type": "multipart/form-data;boundary=----------287032381131322",
    },                    
}
);
  const order = await response.json();
  dispatch();
  return order;
});

const ordersSlice = createSlice({
  name: "orders",
  initialState: {
    loading: false,
    order: null,
  },
  reducers: {
    orderAdded(state, action) {
      Axios.post("http://localhost:8080/api/v1/uploadOrderFile", action.payload,                
      {
        headers: {
            "Content-type": "multipart/form-data;boundary=----------287032381131322",
        },                    
    }
    )
      .then(response => {
          return {order: response, loading:false};
      })
      .catch(error => {
        throw(error);
      });
    },
  },
//   extraReducers: {
